import React, { useEffect, useState, useRef } from 'react';

function LeagueMatchupBanner({ leagueId }) {
  const [matchups, setMatchups] = React.useState([]);
  const [week, setWeek] = React.useState(null);
  const scrollRef = React.useRef(null);

  React.useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => res.json())
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
      })
      .catch(err => console.error('Error loading matchups:', err));
  }, [leagueId]);

  if (!matchups.length) return null;

  const matchupContent = matchups.map((m, i) => {
  const userScore = m.teamA?.points ?? 0;
  const oppScore = m.teamB?.points ?? 0;

  return (
    <React.Fragment key={i}>
      {i !== 0 && (
        <div
          style={{
            width: '2px',
            height: '60%',
            backgroundColor: '#b68b2f', // gold
            margin: '0 8px',
            alignSelf: 'center',
          }}
        />
      )}
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          padding: '0 8px',
          fontSize: '0.75rem',
          whiteSpace: 'nowrap',
          gap: '6px',
          minWidth: '120px',
        }}
      >
        {m.teamA?.avatar && (
          <img
            src={`https://sleepercdn.com/avatars/thumbs/${m.teamA.avatar}`}
            alt={`${m.teamA.name} avatar`}
            style={{ width: 18, height: 18, borderRadius: '50%' }}
          />
        )}
        <span>{m.teamA?.name || 'Unknown'}</span>
        <span
          style={{
            color:
              userScore > oppScore ? '#28a745' : userScore < oppScore ? '#dc3545' : '#f5e96e',
            fontWeight: '600',
          }}
        >
          {userScore}
        </span>

        <span style={{ color: '#aaa' }}>vs</span>

        {m.teamB?.avatar && (
          <img
            src={`https://sleepercdn.com/avatars/thumbs/${m.teamB.avatar}`}
            alt={`${m.teamB.name} avatar`}
            style={{ width: 18, height: 18, borderRadius: '50%' }}
          />
        )}
        <span>{m.teamB?.name || 'Bye'}</span>
        <span
          style={{
            color:
              oppScore > userScore ? '#28a745' : oppScore < userScore ? '#dc3545' : '#f5e96e',
            fontWeight: 'bold',
          }}
        >
          {oppScore}
        </span>
      </div>
    </React.Fragment>
  );
});

  return (
    <div
      style={{
        width: '100vw',
        overflow: 'hidden',
        backgroundColor: '#111',
        borderTop: '2px solid #f2f0e6',
        borderBottom: '2px solid #f2f0e6',
        color: '#eee',
        height: '2.5rem',
        display: 'flex',
        alignItems: 'center',
        position: 'relative',
        fontFamily: 'Segoe UI, sans-serif',
      }}
    >
      <div
        ref={scrollRef}
        style={{
          display: 'flex',
          animation: 'scroll-left 22s linear infinite', // slower than 15s but faster than 30s
          width: 'max-content',
        }}
      >
        <div style={{ display: 'flex' }}>{matchupContent}</div>
        <div style={{ display: 'flex' }}>{matchupContent}</div>
      </div>

      <style>{`
        @keyframes scroll-left {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
      `}</style>
    </div>
  );
}

export default LeagueMatchupBanner;
