import React, { useState, useEffect } from 'react';

function LeagueMatchupBanner({ leagueId }) {
  const [matchups, setMatchups] = useState([]);
  const [week, setWeek] = useState(null);
  const [leagueLogo, setLeagueLogo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load matchups');
        return res.json();
      })
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
        setLeagueLogo(data.leagueLogo);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [leagueId]);

  if (loading) return <p>Loading matchups...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!matchups.length) return <p>No matchups found.</p>;

  return (
    <div
      style={{
        width: '100%',
        overflow: 'hidden',
        backgroundColor: '#222',
        color: '#fff',
        fontFamily: 'Segoe UI, sans-serif',
        whiteSpace: 'nowrap',
        display: 'flex',
        alignItems: 'center',
        height: '10vh', // scales with screen height
        minHeight: 50,
        borderTop: '2px solid #0f9',
        borderBottom: '2px solid #0f9',
        position: 'relative',
        padding: '0 1vw',
      }}
    >

      <div
        style={{
          display: 'inline-flex',
          animation: 'scroll-left 30s linear infinite',
          alignItems: 'center',
        }}
      >
        {matchups.map((m, i) => {
          const userScore = m.user.points;
          const oppScore = m.opponent?.points || 0;
          const result = userScore > oppScore ? 'W' : userScore < oppScore ? 'L' : 'T';

          return (
            <div
              key={i}
              style={{
                padding: '0 2vw',
                borderRight: '1px solid #0f9',
                display: 'flex',
                alignItems: 'center',
                gap: '1vw',
                fontWeight: '600',
                fontSize: 'clamp(0.8rem, 1.5vw, 1.2rem)',
              }}
            >
              <span>{m.user.name}</span>
              <span>{userScore}</span>
              <span>vs</span>
              <span>{m.opponent?.name || 'Bye'}</span>
              <span>{oppScore}</span>
              <span
                style={{
                  color: result === 'W' ? '#0f9' : result === 'L' ? '#f55' : '#ffb',
                }}
              >
                {result}
              </span>
            </div>
          );
        })}
      </div>

      <style>
        {`
          @keyframes scroll-left {
            0% {
              transform: translateX(100%);
            }
            100% {
              transform: translateX(-100%);
            }
          }

          @media (max-width: 600px) {
            div {
              font-size: 0.8rem;
            }
          }
        `}
      </style>
    </div>
  );
}

export default LeagueMatchupBanner;
