import React, { useEffect, useState } from 'react';

function HomePage({ leagueId, userRosterId }) {
  const [matchup, setMatchup] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
  fetch(`http://localhost:5000/api/user-matchup/${leagueId}/${userRosterId}`)
    .then(res => {
      if (!res.ok) throw new Error('Failed to load matchup');
      return res.json();
    })
    .then(data => {
      console.log('Fetched matchup data:', data);  // <-- Add this
      setMatchup(data);
      setLoading(false);
    })
    .catch(err => {
      console.error('Fetch error:', err);  // <-- Add this
      setError(err.message);
      setLoading(false);
    });
}, [leagueId, userRosterId]);

  if (loading) return <p>Loading matchup...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!matchup) return <p>No matchup data found.</p>;

  const userScore = matchup.userMatchup?.points || 0;
  const oppScore = matchup.opponent?.points || 0;
  const result =
    userScore > oppScore ? 'Winning' : userScore < oppScore ? 'Losing' : 'Tied';

  return (
  <div className="container mx-auto p-4">
    <div className="flex justify-center mb-4">
        <img
        src="/logos/LOOGlogo.png"
        alt="League Logo"
        className="h-24 w-auto object-contain"/>
    </div>
    
    
    {/* Ribbon */}
    <div className="flex justify-center mb-6">
        <div className="bg-blue-600 text-white px-6 py-2 rounded-full shadow-lg font-semibold">
            Week 1 â€” You are <span className="underline">{result}</span>
        </div>
    </div>


    <h2 className="text-2xl font-bold mb-4">Week {matchup.week} Matchup</h2>
    <p><strong>{matchup.usernames.user}:</strong> {matchup.userMatchup.points}</p>
    <p><strong>{matchup.usernames.opponent}:</strong> {matchup.opponent?.points}</p>
    <p>Result: <strong>{result}</strong></p>
  </div>
);
}

export default HomePage;
