import React, { useEffect, useState, useRef } from 'react';

function LeagueMatchupBanner({ leagueId }) {
  const [matchups, setMatchups] = useState([]);
  const [week, setWeek] = useState(null);
  const scrollRef = useRef(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => res.json())
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
      })
      .catch(err => console.error('Error loading matchups:', err));
  }, [leagueId]);

  if (!matchups.length) return null;

  const matchupContent = matchups.map((m, i) => {
    const userScore = m.user.points;
    const oppScore = m.opponent?.points || 0;
    const result =
      userScore > oppScore ? 'W' : userScore < oppScore ? 'L' : 'T';

    return (
      <div
        key={i}
        style={{
          padding: '0 2rem',
          borderRight: '1px solid #b68b2f',
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem',
          fontWeight: 600,
          fontSize: '0.95rem',
          whiteSpace: 'nowrap',
        }}
      >
        {m.user.avatar && (
          <img
          src={`https://sleepercdn.com/avatars/thumbs/${m.user.avatar}`}
          alt={`${m.user.name} avatar`}
          style={{ width: 24, height: 24, borderRadius: '50%' }}
  />
)}
<span>{m.user.name}</span>
<span
  style={{
    color:
      userScore > oppScore
        ? '#6fb1fc'  // win color
        : userScore < oppScore
        ? '#f55'     // lose color
        : '#f5e96e', // tie color
    fontWeight: 'bold'
  }}
>
  {userScore}
</span>
<span style={{ color: '#aaa' }}>vs</span>
{m.opponent?.avatar && (
  <img
    src={`https://sleepercdn.com/avatars/thumbs/${m.opponent.avatar}`}
    alt={`${m.opponent.name} avatar`}
    style={{ width: 24, height: 24, borderRadius: '50%' }}
  />
)}
<span>{m.opponent?.name || 'Bye'}</span>
<span
  style={{
    color:
      oppScore > userScore
        ? '#6fb1fc'  // win color
        : oppScore < userScore
        ? '#f55'     // lose color
        : '#f5e96e', // tie color
    fontWeight: 'bold'
  }}
>
  {oppScore}
</span>
      </div>
    );
  });

  return (
    <div
      style={{
        width: '100vw',
        overflow: 'hidden',
        backgroundColor: '#111',
        borderTop: '2px solid #f2f0e6',
        borderBottom: '2px solid #f2f0e6',
        color: '#eee',
        height: '3rem',
        display: 'flex',
        alignItems: 'center',
        position: 'relative',
        fontFamily: 'Segoe UI, sans-serif',
      }}
    >
      <div
        ref={scrollRef}
        style={{
          display: 'flex',
          animation: 'scroll-left 30s linear infinite',
          width: 'max-content',
        }}
      >
        <div style={{ display: 'flex' }}>{matchupContent}</div>
        <div style={{ display: 'flex' }}>{matchupContent}</div>
      </div>

      <style>{`
        @keyframes scroll-left {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
      `}</style>
    </div>
  );
}

export default LeagueMatchupBanner;
