import React, { useState, useEffect } from 'react';

function LeagueMatchupBanner({ leagueId }) {
  const [matchups, setMatchups] = useState([]);
  const [week, setWeek] = useState(null);
  const [leagueLogo, setLeagueLogo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load matchups');
        return res.json();
      })
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
        setLeagueLogo(data.leagueLogo);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [leagueId]);

  if (loading) return <p>Loading matchups...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!matchups.length) return <p>No matchups found.</p>;

  return (
    <div style={{ 
      width: '100vw', 
      overflow: 'hidden', 
      backgroundColor: '#222', 
      color: '#fff', 
      fontFamily: 'Segoe UI', 
      whiteSpace: 'nowrap', 
      display: 'flex', 
      alignItems: 'center',
      height: 50,
      borderTop: '2px solid #0f9',
      borderBottom: '2px solid #0f9',
      position: 'relative',
    }}>
      {leagueLogo && (
        <img 
          src={leagueLogo} 
          alt="League Logo" 
          style={{ height: 40, margin: '0 15px' }} 
        />
      )}

      <div 
        style={{
          display: 'inline-flex',
          animation: 'scroll-left 30s linear infinite',
        }}
      >
        {matchups.map((m, i) => {
          const userScore = m.user.points;
          const oppScore = m.opponent?.points || 0;
          const result = userScore > oppScore ? 'W' : userScore < oppScore ? 'L' : 'T';
          return (
            <div 
              key={i} 
              style={{ 
                padding: '0 20px', 
                borderRight: '1px solid #0f9',
                display: 'flex',
                alignItems: 'center',
                gap: 10,
                fontWeight: 'bold',
                fontSize: 16,
              }}
            >
              <span>{m.user.name}</span>
              <span>{userScore}</span>
              <span>vs</span>
              <span>{m.opponent?.name || 'Bye'}</span>
              <span>{oppScore}</span>
              <span style={{color: result === 'W' ? '#0f9' : result === 'L' ? '#f55' : '#ffb'}}>
                {result}
              </span>
            </div>
          );
        })}
      </div>

      <style>
        {`
          @keyframes scroll-left {
            0% {
              transform: translateX(100%);
            }
            100% {
              transform: translateX(-100%);
            }
          }
        `}
      </style>
    </div>
  );
}

export default LeagueMatchupBanner;
