import React, { useEffect, useState } from 'react';

function HomePage({ leagueId, userRosterId }) {
  const [matchup, setMatchup] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/user-matchup/${leagueId}/${userRosterId}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load matchup');
        return res.json();
      })
      .then(data => {
        console.log('Fetched matchup data:', data);
        setMatchup(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Fetch error:', err);
        setError(err.message);
        setLoading(false);
      });
  }, [leagueId, userRosterId]);

  if (loading) return <p>Loading matchup...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!matchup) return <p>No matchup data found.</p>;

  const userScore = matchup.userMatchup?.points || 0;
  const oppScore = matchup.opponent?.points || 0;
  const result =
    userScore > oppScore ? 'Winning' : userScore < oppScore ? 'Losing' : 'Tied';

  return (
    <div className="container mx-auto p-4 text-center">
      {matchup.leagueLogo && (
        <div
          style={{
            backgroundColor: '#b68b2f',  // Gold accent color
            padding: '8px',
            borderRadius: '8px',
            display: 'inline-block',
            margin: '0 auto 1.5rem',
          }}
        >
          <img
            src={matchup.leagueLogo}
            alt="League Logo"
            style={{
              height: '80px',
              width: 'auto',
              objectFit: 'contain',
              display: 'block',
              margin: '0 auto',
              filter: 'brightness(1.1) contrast(1.2)',
            }}
            onError={e => (e.currentTarget.style.display = 'none')}
          />
        </div>
      )}

      <h2 className="text-2xl font-bold mb-4">Week {matchup.week} Matchup</h2>
      <p>
        <strong>{matchup.usernames.user}:</strong> {userScore}
      </p>
      <p>
        <strong>{matchup.usernames.opponent}:</strong> {oppScore}
      </p>
      <p>
        Result: <strong>{result}</strong>
      </p>
    </div>
  );
}

export default HomePage;
