import React, { useEffect, useState } from 'react';

function HomePage({ leagueId, userRosterId }) {
  const [matchup, setMatchup] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userScore = matchup.userMatchup.points || 0;
const oppScore = matchup.opponent?.points || 0;
const result = userScore > oppScore ? 'Winning' : userScore < oppScore ? 'Losing' : 'Tied';

  useEffect(() => {
    fetch(`http://localhost:5000/api/user-matchup/${leagueId}/${userRosterId}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load matchup');
        return res.json();
      })
      .then(data => {
        setMatchup(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [leagueId, userRosterId]);

  if (loading) return <p>Loading matchup...</p>;
  if (error) return <p>Error: {error}</p>;

  const { week, userMatchup, opponent } = matchup;

  return (
    <div>
      <h2>Week {matchup.week} Matchup</h2>
      <p>{matchup.usernames.user}: {matchup.userMatchup.points}</p>
      <p>{matchup.usernames.opponent}: {matchup.opponent?.points}</p>
      <p>Result: <strong>{result}</strong></p>
    </div>
  );
}

export default HomePage;
