import React, { useEffect, useState, useRef } from 'react';

function LeagueMatchupBanner({ leagueId }) {
  const [matchups, setMatchups] = useState([]);
  const [week, setWeek] = useState(null);
  const scrollRef = useRef(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => res.json())
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
      })
      .catch(err => console.error('Error loading matchups:', err));
  }, [leagueId]);

  if (!matchups.length) return null;

  const matchupContent = matchups.map((m, i) => {
  const userScore = m.teamA?.points ?? 0;
  const oppScore = m.teamB?.points ?? 0;
    const result =
    userScore > oppScore ? 'W' : userScore < oppScore ? 'L' : 'T';

     return (
    <div key={i} style={{ /* styles */ }}>
      {/* team A avatar & name */}
      {m.teamA?.avatar && (
        <img src={`https://sleepercdn.com/avatars/thumbs/${m.teamA.avatar}`} alt={`${m.teamA.name} avatar`} style={{ width: 24, height: 24, borderRadius: '50%' }} />
      )}
      <span>{m.teamA?.name || 'Unknown'}</span>
      <span style={{ color: userScore > oppScore ? '#28a745' : userScore < oppScore ? '#dc3545' : '#f5e96e' }}>
        {userScore}
      </span>

      <span style={{ color: '#aaa' }}>vs</span>

      {/* team B avatar & name */}
      {m.teamB?.avatar && (
        <img src={`https://sleepercdn.com/avatars/thumbs/${m.teamB.avatar}`} alt={`${m.teamB.name} avatar`} style={{ width: 24, height: 24, borderRadius: '50%' }} />
      )}
      <span>{m.teamB?.name || 'Bye'}</span>
      <span style={{
        color: oppScore > userScore ? '#28a745' : oppScore < userScore ? '#dc3545' : '#f5e96e',
        fontWeight: 'bold'
      }}>
        {oppScore}
      </span>
    </div>
  );
});

  return (
    <div
      style={{
        width: '100vw',
        overflow: 'hidden',
        backgroundColor: '#111',
        borderTop: '2px solid #f2f0e6',
        borderBottom: '2px solid #f2f0e6',
        color: '#eee',
        height: '3rem',
        display: 'flex',
        alignItems: 'center',
        position: 'relative',
        fontFamily: 'Segoe UI, sans-serif',
      }}
    >
      <div
        ref={scrollRef}
        style={{
          display: 'flex',
          animation: 'scroll-left 30s linear infinite',
          width: 'max-content',
        }}
      >
        <div style={{ display: 'flex' }}>{matchupContent}</div>
        <div style={{ display: 'flex' }}>{matchupContent}</div>
      </div>

      <style>{`
        @keyframes scroll-left {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
      `}</style>
    </div>
  );
}

export default LeagueMatchupBanner;
