import React from 'react';
import LeagueMatchupCarousel from './LeagueMatchupCarousel'; // Adjust path as needed

function HomePage() {
  const colors = {
    background: '#121212',
    gold: '#b68b2f',
    cream: '#f2f0e6',
    brown: '#6f4f2e'
  };

  const leagueId = '1211169376376459264';

  return (
    <div
      style={{
        backgroundColor: colors.background,
        color: colors.cream,
        minHeight: '100vh',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        padding: '2rem',
        overflowX: 'hidden' // ✅ Fix horizontal scroll issue
      }}
    >
      {/* Header with logo */}
      <header
        style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          marginBottom: '2rem'
        }}
      >
        <div
          style={{
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.5)',
            borderRadius: '12px',
            overflow: 'hidden',
            width: '100px',
            height: '100px',
            marginBottom: '1rem',
            transition: 'transform 0.3s ease'
          }}
          onMouseEnter={e => {
            e.currentTarget.style.transform = 'scale(1.05)';
          }}
          onMouseLeave={e => {
            e.currentTarget.style.transform = 'scale(1)';
          }}
        >
          <img
            src="https://sleepercdn.com/avatars/thumbs/6ad52c4e61b556cd98322c5a7405378e"
            alt="League Logo"
            style={{ width: '100%', height: '100%', objectFit: 'cover', display: 'block' }}
          />
        </div>
        <h1
          style={{
            color: colors.gold,
            fontSize: '2.5rem',
            fontWeight: 'bold',
            letterSpacing: '0.1em',
            textAlign: 'center'
          }}
        >
          League of Ordinary Gentlemen
        </h1>
      </header>

      {/* Ribbon */}
      <div
        style={{
          backgroundColor: colors.gold,
          color: colors.background,
          padding: '0.75rem 1.5rem',
          borderRadius: '30px',
          width: 'fit-content',
          margin: '0 auto 3rem auto',
          fontWeight: '600',
          letterSpacing: '0.1em',
          textTransform: 'uppercase',
          boxShadow: `0 0 15px ${colors.gold}`
        }}
      >
        Week 1 Matchup
      </div>

      {/* Carousel container forced to screen width */}
      <div style={{ width: '100vw', marginLeft: '-2rem' }}>
        <LeagueMatchupCarousel leagueId={leagueId} />
      </div>

      {/* Footer */}
      <footer
        style={{
          textAlign: 'center',
          marginTop: '4rem',
          fontSize: '0.875rem',
          color: colors.brown,
          letterSpacing: '0.05em'
        }}
      >
        &copy; 2025 League of Ordinary Gentlemen
      </footer>
    </div>
  );
}

function Announcement({ messages }) {
  return (
    <div
      style={{
        backgroundColor: '#222',
        color: '#f2f0e6',
        border: '2px solid #b68b2f',
        borderRadius: '12px',
        padding: '1rem 2rem',
        marginBottom: '2rem',
        fontSize: '1.1rem',
        fontWeight: '600',
        maxWidth: '600px',
        marginLeft: 'auto',
        marginRight: 'auto',
      }}
    >
      {messages.length === 0 ? (
        <p>No announcements at this time.</p>
      ) : (
        messages.map((msg, i) => (
          <p key={i} style={{ marginBottom: i < messages.length - 1 ? '1rem' : 0 }}>
            {msg}
          </p>
        ))
      )}
    </div>
  );
}

export default HomePage;
