import React, { useState, useEffect } from 'react';

function LeagueMatchupCarousel({ leagueId }) {
  const [matchups, setMatchups] = useState([]);
  const [week, setWeek] = useState(null);
  const [leagueLogo, setLeagueLogo] = useState(null);
  const [index, setIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load matchups');
        return res.json();
      })
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
        setLeagueLogo(data.leagueLogo);
        setLoading(false);
        setIndex(0);  // reset index on new data
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [leagueId]);

  // Auto-slide effect
  useEffect(() => {
    if (matchups.length === 0) return;

    const interval = setInterval(() => {
      setIndex(i => (i === matchups.length - 1 ? 0 : i + 1));
    }, 3000); // Change slide every 3 seconds

    return () => clearInterval(interval); // cleanup on unmount or dependency change
  }, [matchups]);

  if (loading) return <p>Loading matchups...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!matchups.length) return <p>No matchups found.</p>;

  const currentMatchup = matchups[index];
  const userScore = currentMatchup.user.points;
  const oppScore = currentMatchup.opponent?.points || 0;
  const result = userScore > oppScore ? 'Winning' : userScore < oppScore ? 'Losing' : 'Tied';

  function prev() {
    setIndex(i => (i === 0 ? matchups.length - 1 : i - 1));
  }

  function next() {
    setIndex(i => (i === matchups.length - 1 ? 0 : i + 1));
  }

  return (
    <div style={{ maxWidth: 400, margin: '2rem auto', textAlign: 'center', fontFamily: 'Segoe UI' }}>
      <h2 style={{ marginBottom: '1rem' }}>Week {week} Matchup</h2>

      <div style={{ marginBottom: 20 }}>
        <p><strong>{currentMatchup.user.name}:</strong> {userScore}</p>
        <p><strong>{currentMatchup.opponent?.name || 'Bye'}:</strong> {oppScore}</p>
        <p>Result: <strong>{result}</strong></p>
      </div>

      <div>
        <button onClick={prev} style={{ marginRight: 10 }}>Prev</button>
        <button onClick={next}>Next</button>
      </div>

      <p style={{ marginTop: 15, fontSize: 12, color: '#666' }}>
        Showing matchup {index + 1} of {matchups.length}
      </p>
    </div>
  );
}

export default LeagueMatchupCarousel;
