import React, { useState, useEffect } from 'react';

function LeagueMatchupBanner({ leagueId }) {
  const [matchups, setMatchups] = useState([]);
  const [week, setWeek] = useState(null);
  const [leagueLogo, setLeagueLogo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load matchups');
        return res.json();
      })
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
        setLeagueLogo(data.leagueLogo);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [leagueId]);

  if (loading) return <p>Loading matchups...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!matchups.length) return <p>No matchups found.</p>;

  return (
    <div
      style={{
        width: '100%',
        overflow: 'hidden',
        backgroundColor: '#1e1e1e',
        color: '#fff',
        fontFamily: 'Segoe UI, sans-serif',
        whiteSpace: 'nowrap',
        display: 'flex',
        alignItems: 'center',
        height: 60, // fixed height
        borderTop: '2px solid #6fb1fc',   // soft blue (customize as needed)
        borderBottom: '2px solid #6fb1fc',
        position: 'relative',
        paddingLeft: '1rem',
        paddingRight: '1rem',
        boxSizing: 'border-box',
      }}
    >
      {leagueLogo && (
        <img
          src={leagueLogo}
          alt="League Logo"
          style={{
            height: '40px',
            marginRight: '1rem',
            objectFit: 'contain',
          }}
        />
      )}

      <div
        style={{
          display: 'inline-flex',
          animation: 'scroll-left 30s linear infinite',
          alignItems: 'center',
        }}
      >
        {matchups.map((m, i) => {
          const userScore = m.user.points;
          const oppScore = m.opponent?.points || 0;
          const result = userScore > oppScore ? 'W' : userScore < oppScore ? 'L' : 'T';

          return (
            <div
              key={i}
              style={{
                padding: '0 2rem',
                borderRight: '1px solid #444',
                display: 'flex',
                alignItems: 'center',
                gap: '1rem',
                fontWeight: '600',
                fontSize: '1rem',
              }}
            >
              <span>{m.user.name}</span>
              <span>{userScore}</span>
              <span>vs</span>
              <span>{m.opponent?.name || 'Bye'}</span>
              <span>{oppScore}</span>
              <span
                style={{
                  color: result === 'W' ? '#6fb1fc' : result === 'L' ? '#f55' : '#ffb',
                }}
              >
                {result}
              </span>
            </div>
          );
        })}
      </div>

      <style>
        {`
          @keyframes scroll-left {
            0% {
              transform: translateX(100%);
            }
            100% {
              transform: translateX(-100%);
            }
          }
        `}
      </style>
    </div>
  );
}

export default LeagueMatchupBanner;
