import React, { useEffect, useState } from 'react';

function HomePage({ leagueId, userRosterId }) {
  const [matchupData, setMatchupData] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    async function fetchMatchup() {
      try {
        const res = await fetch(`/api/user-matchup/${leagueId}/${userRosterId}`);
        if (!res.ok) throw new Error('Failed to fetch matchup');
        const data = await res.json();
        setMatchupData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchMatchup();
  }, [leagueId, userRosterId]);

  if (loading) return <div>Loading matchup...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!matchupData || !matchupData.userMatchup) return <div>No matchup data available.</div>;

  const { userMatchup, opponent, usernames, week } = matchupData;

  return (
    <div>
      <h2>Week {week} Matchup</h2>
      <p>{usernames.user}: {userMatchup.points.toFixed(2)}</p>
      <p>{usernames.opponent}: {opponent?.points.toFixed(2) ?? 'N/A'}</p>
      {/* You can also display Result or other info here */}
    </div>
  );
}
