import React, { useEffect, useState } from 'react';

function LeagueMatchupBanner({ leagueId }) {
  const [matchups, setMatchups] = useState([]);
  const [week, setWeek] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/league-matchups/${leagueId}`)
      .then(res => res.json())
      .then(data => {
        setMatchups(data.matchups);
        setWeek(data.week);
      })
      .catch(err => console.error('Error loading matchups:', err));
  }, [leagueId]);

  if (!matchups.length) return null;

  const bannerStyle = {
    width: '100vw',
    overflow: 'hidden',
    backgroundColor: '#111',
    borderTop: '2px solid #222',
    borderBottom: '2px solid #222',
    color: '#eee',
    height: '3rem',
    display: 'flex',
    alignItems: 'center',
    fontFamily: 'Segoe UI, sans-serif',
    position: 'relative',
  };

  const scrollContainerStyle = {
    display: 'flex',
    width: 'max-content',
    animation: 'scroll-left 30s linear infinite',
  };

  const matchupItemStyle = {
    padding: '0 2rem',
    borderRight: '1px solid #333',
    display: 'flex',
    alignItems: 'center',
    gap: '1rem',
    fontWeight: '600',
    fontSize: '0.95rem',
    whiteSpace: 'nowrap',
  };

  return (
    <div style={bannerStyle}>
      <div style={scrollContainerStyle}>
        {[...matchups, ...matchups].map((m, i) => {
          const userScore = m.user.points;
          const oppScore = m.opponent?.points || 0;
          const result =
            userScore > oppScore ? 'W' : userScore < oppScore ? 'L' : 'T';

          return (
            <div key={i} style={matchupItemStyle}>
              <span>{m.user.name}</span>
              <span>{userScore}</span>
              <span style={{ color: '#aaa' }}>vs</span>
              <span>{m.opponent?.name || 'Bye'}</span>
              <span>{oppScore}</span>
              <span
                style={{
                  color:
                    result === 'W'
                      ? '#6fb1fc'
                      : result === 'L'
                      ? '#f55'
                      : '#f5e96e',
                }}
              >
                {result}
              </span>
            </div>
          );
        })}
      </div>

      <style>
        {`
          @keyframes scroll-left {
            0% {
              transform: translateX(0%);
            }
            100% {
              transform: translateX(-50%);
            }
          }
        `}
      </style>
    </div>
  );
}

export default LeagueMatchupBanner;
