import React, { useEffect, useState } from 'react';

function HomePage({ leagueId, userRosterId }) {
  const [matchup, setMatchup] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/user-matchup/${leagueId}/${userRosterId}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to load matchup');
        return res.json();
      })
      .then(data => {
        setMatchup(data);
        setLoading(false);
      }).then(data => {
  console.log('Matchup data:', data);  // Check if leagueLogo exists and is a valid URL
  setMatchup(data);
  setLoading(false);
})
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [leagueId, userRosterId]);

  if (loading) return <p>Loading matchup...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!matchup) return <p>No matchup data found.</p>;

  const userScore = matchup.userMatchup?.points || 0;
  const oppScore = matchup.opponent?.points || 0;
  const result =
    userScore > oppScore ? 'Winning' : userScore < oppScore ? 'Losing' : 'Tied';

  return (
  <div className="max-w-md mx-auto p-4">
    {/* League Logo */}
    {matchup.leagueLogo && (
      <div className="flex justify-center mb-6">
        <img
          src={matchup.leagueLogo}
          alt="League Logo"
          className="h-16 w-auto object-contain"
        />
      </div>
    )}

    <h2 className="text-2xl font-semibold mb-4">Week {matchup.week} Matchup</h2>

    <p className="text-lg mb-2">
      <strong>{matchup.usernames.user}:</strong> {matchup.userMatchup.points}
    </p>

    <p className="text-lg mb-4">
      <strong>{matchup.usernames.opponent}:</strong> {matchup.opponent?.points}
    </p>

    <p className="text-xl font-bold">
      Result: <span>{result}</span>
    </p>
  </div>
);
}

export default HomePage;
